import React, { useState } from "react";
import styles from "./styles.module.css";
import { useQuery, gql } from "@apollo/client";
import { Button } from "reactstrap";
import "bootstrap/dist/css/bootstrap.min.css";

const ALL_JOBS = gql`
  query {
    jobs {
      _id
      title
      companyName
      place
      emailCompany
      phoneCompany
      body
      createdAt
    }
  }
`;

const eliminarVacante = (e) => {
  const id = e.currentTarget.getAttribute("data-id");
  alert(id);
};
const Main = () => {
  const handleLogout = () => {
    localStorage.removeItem("token");
    window.location.reload();
  };

  const { data, error, loading } = useQuery(ALL_JOBS);

  const [searchTeam, setSearchTeam] = useState("");

  if (error) return <span style="color: red">{error}</span>;

  return (
    <div className={styles.main_container}>
      <nav className={styles.navbar}>
        <h1>Bolsa de Trabajo</h1>
        <button className={styles.white_btn} onClick={handleLogout}>
          <img src="data:image/png;base64," />
        </button>
      </nav>
      <br></br>

      <br></br>
      <div className="container mt-4 d-grid gap-5">
        <Button
          data-bs-target="#modalAñadir"
          data-bs-toggle="modal"
          variant="primary"
        >
          Agregar
        </Button>
      </div>
      <div className="container mt-5 table-responsive">
        <div className="form-outline mb-4">
          <input
            type="search"
            className="form-control"
            id="datatable-search-input"
            placeholder="Buscar..."
            onChange={(event) => {
              setSearchTeam(event.target.value);
            }}
          />
        </div>

        <table className="table table-hover">
          <caption>Lista De Vacan</caption>
          <thead>
            <tr>
              <th>_id</th>
              <th>title</th>
              <th>companyName</th>
              <th>place</th>
              <th>emailCompany</th>
              <th>phoneCompany</th>
              <th>body</th>
              <th>createdAt</th>
              <th>Aciones</th>
            </tr>
          </thead>
          <tbody>
            {loading ? (
              <p>Loading....</p>
            ) : (
              <>
                {data &&
                  data.jobs
                    .filter((job) => {
                      if (searchTeam == "") {
                        return job;
                      } else if (
                        job.title
                          .toLowerCase()
                          .includes(searchTeam.toLowerCase())
                      ) {
                        return job;
                      }
                    })
                    .map((job) => (
                      <tr key={job._id}>
                        <td>{job._id}</td>
                        <td>{job.title}</td>
                        <td>{job.companyName}</td>
                        <td>{job.place}</td>
                        <td>{job.emailCompany}</td>
                        <td>{job.phoneCompany}</td>
                        <td>{job.body}</td>
                        <td>{job.createdAt}</td>
                        <td>
                          <button
                            type="button"
                            data-bs-whatever={job._id}
                            data-bs-target="#modalEliminarVacante"
                            data-bs-toggle="modal"
                            class="btn btn-danger"
                          >
                            <img src="data:image/png;base64," />
                            Eliminar
                          </button>{" "}
                          <button
                            type="button"
                            data-bs-whatever={job._id}
                            data-title={job.title}
                            data-company={job.companyName}
                            data-place={job.place}
                            data-email={job.emailCompany}
                            data-phone={job.phoneCompany}
                            data-body={job.body}
                            data-bs-target="#modalEditar"
                            data-bs-toggle="modal"
                            class="btn btn-primary border"
                          >
                            <img src="data:image/png;base64," />
                            Actualizar
                          </button>
                        </td>
                      </tr>
                    ))}
              </>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Main;
