import styles from "./styles.module.css";
import { useQuery, gql } from "@apollo/client";
import { Button } from "reactstrap";
import "bootstrap/dist/css/bootstrap.min.css";
const ALL_JOBS = gql`
  query {
    jobs {
      _id
      title
      companyName
      place
      emailCompany
      phoneCompany
      body
      createdAt
    }
  }
`;
var modalElimina = document.getElementById("modalEliminarLibro");
modalElimina.addEventListener("show.bs.modal", function (event) {
  // Button that triggered the modal
  var button = event.relatedTarget;
  // Extract info from data-bs-* attributes
  var recipient = button.getAttribute("data-bs-whatever");

  var element3 = document.getElementById("eliminarLibro");
  element3.onclick = function (event) {
    //validamos el login
    var xhr = new XMLHttpRequest();
    xhr.open("POST", "eliminarLibroSitio.php", true);
    xhr.setRequestHeader("Content-type", "application/x-www-form-urlencoded");
    xhr.onload = function () {
      if (this.responseText = "success") {
        window.location.href = "eliminarLibro.php?id=" + recipient;
      } else {
        alert("Hubo un error al ejecutar la operacion, revisa tu conexion a internet");
      }
    };
    xhr.send(
      "id=" +
      recipient
    );

  };
});

const eliminarVacante = (e) => {
  const id = e.currentTarget.getAttribute("data-id")
  alert(id);
}
const Main = () => {
  const handleLogout = () => {
    localStorage.removeItem("token");
    window.location.reload();
  };

  const { data, error, loading } = useQuery(ALL_JOBS);

  if (error) return <span style="color: red">{error}</span>;

  return (
    <div className={styles.main_container}>
      <nav className={styles.navbar}>
        <h1>Bolsa de Trabajo</h1>
        <button className={styles.white_btn} onClick={handleLogout}>
          <img src="data:image/png;base64," />
        </button>
      </nav>
      <br></br>
      <div className="mb-4 d-grid gap-5">
        <Button variant="primary">Agregar</Button>
      </div>
      <div className="table-responsive">
        <table className="table table-hover">
          <thead>
            <tr>
              <th>_id</th>
              <th>title</th>
              <th>companyName</th>
              <th>place</th>
              <th>emailCompany</th>
              <th>phoneCompany</th>
              <th>body</th>
              <th>createdAt</th>
              <th>Aciones</th>
            </tr>
          </thead>
          <tbody>
            {loading ? (
              <p>Loading....</p>
            ) : (
              <>
                {data &&
                  data.jobs.map((job) => (
                    <tr key={job._id}>
                      <td>{job._id}</td>
                      <td>{job.title}</td>
                      <td>{job.companyName}</td>
                      <td>{job.place}</td>
                      <td>{job.emailCompany}</td>
                      <td>{job.phoneCompany}</td>
                      <td>{job.body}</td>
                      <td>{job.createdAt}</td>
                      <td>
                        <button type="button" data-bs-whatever={job._id} data-bs-target="#modalEliminarVacante" data-bs-toggle="modal" class="btn btn-danger">
                          Eliminar
                        </button>{" "}
                        <button type="button" class="btn btn-primary">
                          Actualizar
                        </button>
                      </td>
                    </tr>
                  ))}
              </>
            )}
          </tbody>
        </table>
      </div>
      <div class="modal fade" id="modalEliminarVacante" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
        <div class="modal-dialog">
          <div class="modal-content">
            <div class="modal-header">
              <h5 class="modal-title" id="exampleModalLabel">Confirmar</h5>
              <button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
            </div>
            <div class="modal-body">
              ¿Estas seguro de eliminar esta vacante?
            </div>
            <div class="modal-footer">
              <button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Cancelar</button>
              <button type="button" class="btn btn-primary" onClick={eliminarVacante} data-id={data - bs - target}>Confirmar</button>
            </div>
          </div>
        </div>
      </div>
    </div >
  );
};

export default Main;
